package uz.coder.hilt.adapters.pager_adaptersimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport uz.coder.hilt.ui.news.AllNewsFragmentimport uz.coder.hilt.ui.news.SavedNewsFragmentclass NewsViewPager(    fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int {        return 2    }    override fun createFragment(position: Int): Fragment {        return when (position) {            0 -> AllNewsFragment()            else -> SavedNewsFragment()        }    }}