package uz.coder.hilt.adapters.pager_adaptersimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.fragment.app.FragmentPagerAdapterimport uz.coder.hilt.ui.splash.SplashFragmentclass SplashViewPagerAdapter(fragmentManager: FragmentManager) : FragmentPagerAdapter(    fragmentManager,    BEHAVIOR_RESUME_ONLY_CURRENT_FRAGMENT) {    override fun getCount(): Int {        return 3    }    override fun getItem(position: Int): Fragment {        return SplashFragment(position)    }}