package uz.coder.hilt.adapters.pager_adaptersimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport uz.coder.hilt.ui.statistic.CountryFragmentimport uz.coder.hilt.ui.statistic.GlobalFragmentclass StatisticViewPager(    fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int {        return 2    }    override fun createFragment(position: Int): Fragment {        return when (position) {            0 -> CountryFragment()            else -> GlobalFragment()        }    }}