package uz.coder.hilt.database.daoimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategy.REPLACEimport androidx.room.Queryimport kotlinx.coroutines.flow.Flowimport uz.coder.hilt.database.entity.NewsEntity@Daointerface NewsDao {    @Insert    suspend fun insertNews(entity: NewsEntity)    @Insert(onConflict = REPLACE)    suspend fun insertNews(entity: List<NewsEntity>)    @Query("UPDATE newsentity SET save = 1 where url =:url")    suspend fun insertSavedNews(url: String)    @Query("select * from NewsEntity")    suspend fun getNews(): List<NewsEntity>    @Query("select * from NewsEntity where save = 1")    fun getSavedNews(): Flow<List<NewsEntity>>}