package uz.coder.hilt.database.entityimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKeyimport java.io.Serializable@Entitydata class NewsEntity(    @PrimaryKey    val url: String,    @ColumnInfo(name = "author")    val imageUrl: String,    @ColumnInfo(name = "image")    val author: String = "OOO",    @ColumnInfo(name = "title")    val title: String,    @ColumnInfo(name = "description")    val description: String,    @ColumnInfo(name = "content")    val content: String,    @ColumnInfo(name = "publishedAt")    val publishedAt: String,    @ColumnInfo(name = "save")    var isSave: Int):Serializable