package uz.coder.hilt.nerworkingimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Queryimport uz.coder.hilt.models.news.Newsinterface ApiService {    @GET("everything")    suspend fun getNews(        @Query("q") q:String = "covid",        @Query("sortBy") sortBy:String = "publishedAt",        @Query("apiKey") apiKey:String = "9062130b56ca4be4935e86b52df2b9f4",    ):Response<News>}